SELECT p.id as personid, CONCAT_WS(' ',CONCAT_WS(' ',p.name_ar,CONCAT('\"',p.altname_ar,'\"')),p.suffix_ar) as firstname_ar, CONCAT_WS(' ',CONCAT_WS(' ',p.name_en,CONCAT('\"',p.altname_en,'\"')),p.suffix_en) as firstname_en, f.name_ar as lastname_ar, f.name_en as lastname_en, p.gender as gender, GROUP_CONCAT(s.id ORDER BY s.dob SEPARATOR '،') as spouseid, GROUP_CONCAT(CONCAT_WS(' ',CONCAT_WS(' ',CONCAT_WS(' ',s.name_ar,CONCAT('\"',s.altname_ar,'\"')),s.suffix_ar),sf.name_ar) ORDER BY s.dob SEPARATOR '،') as spousename_ar, s.suffix_ar as spousesuffix_ar, sf.name_ar as spousefamily_ar FROM person p LEFT JOIN `marriage` m ON p.gender = 'male' AND m.husband = p.id OR p.gender = 'female' AND m.wife = p.id LEFT JOIN `person` s ON p.gender = 'male' AND m.wife = s.id OR p.gender = 'female' AND m.husband = s.id LEFT JOIN `family` sf ON s.family = sf.id JOIN family f ON p.family = f.id WHERE p.father = '750' OR p.mother = '750' GROUP BY p.id ORDER BY -p.dob DESC, p.id